import { useState } from "react";
import { Link } from "react-router-dom";
import { FaBuilding, FaCertificate, FaGavel, FaGraduationCap, FaPhoneSquare, FaPhotoVideo } from "react-icons/fa";

function Header() {
  const [mainNav, setMainNav] = useState([
    { to: "#", icon: <FaCertificate />, text: "Profile" },
    {
      to: "#",
      icon: <FaGavel />,
      text: "Services",
    },
    {
      to: "#",
      icon: <FaBuilding />,
      text: "Projects",
    },
    {
      to: "#",
      icon: <FaPhotoVideo />,
      text: "Gallery",
    },
    {
      to: "#",
      icon: <FaPhoneSquare />,
      text: "Contacts",
    },
    {
      to: "#",
      icon: <FaGraduationCap />,
      text: "Careers",
    },
  ]);
  return (
    <div className="header">
      <div className="container">
        <div className="header-flex hide-on-small">
          <div className="nav-header">
            {mainNav.map((link, index) => (
              <div key={index}>
                <Link to={link.to} >
                  <i>{link.icon}</i>
                  {link.text}
                </Link>
              </div>
            ))}
          </div>
        </div>

        <div className="slogan">
          <h1>Bwap constructions Company limited</h1>
          <p>From concept to creation</p>
        </div>
      </div>
    </div>
  );
}

export default Header;
