import { useState } from "react";
import { Link } from "react-router-dom";
import {
  FaBars,
  FaBuilding,
  FaCertificate,
  FaGavel,
  FaGraduationCap,
  FaPhoneSquare,
  FaPhotoVideo,
  FaTimes,
} from "react-icons/fa";

function Header() {
  const showMenu = () => {
    setMenu(!menu)
  }
  const [mainNav, setMainNav] = useState([
    { to: "#", icon: <FaCertificate />, text: "Profile" },
    {
      to: "#",
      icon: <FaGavel />,
      text: "Services",
    },
    {
      to: "#",
      icon: <FaBuilding />,
      text: "Projects",
    },
    {
      to: "#",
      icon: <FaPhotoVideo />,
      text: "Gallery",
    },
    {
      to: "#",
      icon: <FaPhoneSquare />,
      text: "Contacts",
    },
    {
      to: "#",
      icon: <FaGraduationCap />,
      text: "Careers",
    },
  ]);
  const [menu,setMenu] = useState(true)


  return (
    <div className="header">
      <div className="container">
        <div className="header-flex">
          <div className="hide-on-small">
            <div className="nav-header">
              {mainNav.map((link, index) => (
                <div key={index}>
                  <Link to={link.to}>
                    <i>{link.icon}</i>
                    {link.text}
                  </Link>
                </div>
              ))}
            </div>
          </div>

        </div>
        <div className="hide-on-big">
            <div className="mobile-nav">
              <i onClick={showMenu}>{menu ? <FaBars /> : <FaTimes />}</i>
              {!menu && <div className='menu'>
                <p>yooo</p>
                <p>UUUu</p>
                </div>}
            </div>
          </div>
        <div className="slogan">
          <h1>Bwap constructions Company limited</h1>
          <p>From concept to creation</p>
        </div>
      </div>
    </div>
  );
}

export default Header;
