import { useState } from "react";
import ham from "../img/ham.jpeg";
import stadium from "../img/stadium.jpeg";
import greenhill from "../img/greenhill.jpeg";
import pentecostal from "../img/pentecostal.jpeg";
import flowershop from "../img/flowershop.jpeg";
import nol from "../img/nol.jpeg";
import Overlay from "../components/Overlay";
const Projects = () => {
  const [overlay, setOverlay] = useState(true);
  const [pic, setPic] = useState({
    src: stadium,
    text: " Nakivubo Stadium",
  });

  const toggleProject = () => {
    setOverlay(!overlay);
  };

  const images = [
    {
      src: stadium,
      text: " Nakivubo Stadium",
    },
    {
      src: greenhill,
      text: " GreenHill Academy",
    },
    {
      src: pentecostal,
      text: " Pentecostal Church",
    },
    {
      src: nol,
      text: " NOL-PJ",
    },
    {
      src: ham,
      text: " Ham Enterprises Limited Headquarters",
    },
    {
      src: flowershop,
      text: " Flower Shop",
    },
  ];

  return (
    <div className="projects">
      <div className="container">
        <div className="projects-flex">
          {images.map((image, index) => {
            return (
              <div className="project-item " key={index}>
                <figure>
                  <img src={image.src} alt="" onClick={toggleProject} />
                  <figcaption>
                    <h3>{image.text}</h3>
                  </figcaption>
                </figure>
              </div>
            );
          })}
        </div>
        {overlay && <Overlay onClick={toggleProject} />}
      </div>
      <button onClick={toggleProject}>Change</button>
    </div>
  );
};
export default Projects;
